const nameInput=document.querySelector("#pseudo"),msgInput=document.querySelector("#msg"),checkButton=document.querySelector("#buttonInput"),messageContainer=document.querySelector(".touit_content");function addTouit(i,s){if(s){let e=document.createElement("article");e.className="touit";let t=document.createElement("p");t.className="touit_pseudo";i=document.createTextNode(i);t.appendChild(i),e.appendChild(t);let n=document.createElement("p");n.className="touit_message";s=document.createTextNode(s);n.appendChild(s),e.appendChild(n);let c=document.createElement("div");c.className="touit_icone_box";let a=document.createElement("button");a.className="like";let d=document.createElement("img");d.src="src/img/like.svg";let l=document.createElement("button");l.className="comment";let m=document.createElement("img");m.src="src/img/comment.svg";let o=document.createElement("button");o.className="close";let u=document.createElement("img");u.src="src/img/close.svg",c.appendChild(a),a.appendChild(d),c.appendChild(l),l.appendChild(m),c.appendChild(o),o.appendChild(u),e.appendChild(c),messageContainer.prepend(e),o.addEventListener("click",function(){messageContainer.removeChild(e)})}}checkButton.addEventListener("click",function(e){e.preventDefault(),addTouit(nameInput.value,msgInput.value)});